import axios from "axios";
import React from "react"



class Followers extends React.Component {
    state = {
        followers: [],
    };

    componentDidMount(){
        axios.get("https://api.github.com/users/jessejohns-1/followers")
        .then((res) => {
           this.setState({
               followers: res.data
           })
        })
        .catch((err) => {
            console.log(err)
        })
    }

    render(){
        return(
            <div>
               <h1>Hellow</h1>
            </div>
        )
    }
}
export default Followers;
