import React from "react";
import axios from "axios";


class GitUser extends React.Component{

    state = {
        bananas: [],
    };

    componentDidMount(){
        axios
        .get("https://api.github.com/users/jessejohns-1")
        .then ((res) => {
            this.setState({
                bananas: res.data,
            })
        })
        .catch((err) => {
            console.log(err)
        })
    }




    render(){
        return(
            <div>
                <h2>{this.state.bananas.login}</h2>
                <h2>{this.state.bananas.login}</h2>
            </div>
        )
    }
}



export default GitUser
